import React,{useState} from "react";
import "../style/widget.css";

const Widget = () => {

    const[showPopup,setShowPopupState]=useState(false);
    const[travelMode,setTravelModeState]=useState('All');

    const popupVisible=()=>
    {
        setShowPopupState(true);
    }

    const hidePopup=()=>
    {
        setShowPopupState(false);
    }


  return (
    <>
      <div className="card-container">

      <div className="search-bar-container">
          <input
            type="text"
            name=""
            id="search"
            placeholder="Search for the name of a bus service, flight service or a hotel"
            className="search-box" autoComplete="off" onFocus={popupVisible}
          />
          <input type="submit" value="Search" name="submit" onClick={hidePopup} />
        </div>

        {showPopup && (
        <section className="travel-packages">
          <div className="travel-mode">
            <input type="radio" className="travel-mode-btn" id="Buses" name="category" value="Buses" 
            onChange={(a)=>setTravelModeState(a.target.value)}/>
            <label htmlFor="Buses">Buses</label>
          </div>
          <div className="travel-mode">
            <input type="radio" className="travel-mode-btn" id="Flights" name="category" value="Flights" 
            onChange={(b)=>setTravelModeState(b.target.value)}/>
            <label htmlFor="Flights">Flights</label>
          </div>
          <div className="travel-mode">
            <input type="radio" className="travel-mode-btn" id="Hotels" name="category" value="Hotels" 
            onChange={(c)=>setTravelModeState(b.target.value)}/>
            <label htmlFor="Hotels">Hotels</label>
          </div>
          <div className="travel-mode">
            <input type="radio" className="travel-mode-btn" id="All" name="category" value="All" 
            onChange={(d)=>setTravelModeState(d.target.value)}/>
            <label htmlFor="All">All</label>
          </div>
        </section>)
        }


      </div>
    </>
  );
};

export default Widget;
